import React, { useState } from "react";
import UserCard from "./UserCard";

const Users = () => {
  const [users, setUsers] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { latitude, longitude } = e.target;

    const response = await fetch(
      `http://localhost:3001/users/?lat=${latitude.value}&lng=${longitude.value}`
    );
    const data = await response.json();
    setUsers(data);

    e.target.reset();
  };
  return (
    <div className="flex flex-col">
      <form
        className="bg-[#F0ECCF] shadow-md rounded px-8 pt-6 pb-8 mb-4 flex-col lg:flex-row"
        onSubmit={handleSubmit}
      >
        <h2 className="font-bold text-lg p-2 text-gray-500">
          Enter the location in latitudes and longitudes in format of Decimal
          Degrees(DD) to find Users in your area
        </h2>
        <label
          className="text-gray-500 text-sm font-bold mb-2 py-4"
          htmlFor="latitude"
        >
          Latitude:
        </label>
        <input
          className="shadow appearance-none border rounded my-2 w-full py-2 px-3 text-gray-500 leading-tight focus:outline-none focus:shadow-outline"
          name="latitude"
          type="text"
          pattern="-?[0-9]{1,3}\.[0-9]*"
          required
        />
        <label
          className="text-gray-500 text-sm font-bold mb-2 mt-2 py-4"
          htmlFor="longitude"
        >
          Longitude:
        </label>
        <input
          className="shadow appearance-none border rounded w-full mt-2 py-2 px-3 text-gray-500 leading-tight focus:outline-none focus:shadow-outline"
          name="longitude"
          type="text"
          pattern="-?[0-9]{1,3}\.[0-9]*"
          required
        />
        <button
          className="bg-[#3C6255] mt-4 text-[#F0ECCF] hover:text-[#3C6255] hover:bg-[#F0ECCF] font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          type="submit"
        >
          Confirm User
        </button>
      </form>

      <div>
        <h1 className="font-bold text-3xl text-[#F0ECCF]">Users</h1>
        {users.map((user) => (
          <UserCard key={user._id} user={user} />
        ))}
      </div>
    </div>
  );
};

export default Users;
